NUMBER_TWO = ""

import framebuf

def hex_string_to_bit_array(hex_string):
    bit_array = []
    for i in range(0, len(hex_string), 2):
        byte = int(hex_string[i:i+2], 16)
        for j in range(8):
            bit_array.append((byte >> (7 - j)) & 1)
    return bit_array

def overlay_bit_image(framebuffer: framebuf.FrameBuffer, x_offset, y_offset, width=60, height=80):
    bit_array = hex_string_to_bit_array(NUMBER_TWO)

    if len(bit_array) < width * height:
        raise ValueError("Hex string does not contain enough bits for a 60x80 image")

    i = 0
    for y in range(height):
        for x in range(width):
            bit = bit_array[i]
            framebuffer.pixel(x + x_offset, y + y_offset, 0x00 if bit == 1 else 0xFF)
            i += 1
